// Error handling for the entire monitoring system
process.on('uncaughtException', (error) => {
  console.error('üö® MONITORING SYSTEM CRITICAL ERROR:', error);
  console.log('Attempting emergency restart...');
  
  setTimeout(() => {
    new ContinuousErrorReporter().startContinuousMonitoring();
  }, 5000);
});

process.on('unhandledRejection', (reason, promise) => {
  console.error('üö® MONITORING SYSTEM REJECTION:', reason);
});

// Graceful shutdown
process.on('SIGINT', () => {
  console.log('\nüõë Shutting down continuous monitoring...');
  console.log('üìÑ Final report will be generated...');
  process.exit(0);
});

// CLI interface
if (require.main === module) {
  const reporter = new ContinuousErrorReporter();
  
  const args = process.argv.slice(2);
  
  if (args.includes('--daemon')) {
    // Run as daemon
    console.log('üöÄ Starting as daemon process...');
    reporter.startContinuousMonitoring().catch(error => {
      console.error('‚ùå Failed to start monitoring:', error);
      process.exit(1);
    });
  } else {
    // Interactive mode
    console.log('üöÄ Starting interactive monitoring...');
    reporter.startContinuousMonitoring().then(() => {
      console.log('‚úÖ Monitoring active. Press Ctrl+C to stop.');
    }).catch(error => {
      console.error('‚ùå Failed to start monitoring:', error);
      process.exit(1);
    });
  }
}

module.exports = ContinuousErrorReporter;